import React, { useState } from 'react';
import FormRegistro from './componentes/FormRegistro';
import FormLogin from './componentes/FormLogin';
import Chat from './componentes/Chat'; // Importa el componente de Chat
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

const App = () => {
  const [activeTab, setActiveTab] = useState('registro');

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="container" style={{
      position: 'fixed',
      top: '10px',
      right: '10px', 
      maxHeight: '650px', 
      overflow: 'auto', 
    }}>
      <div className="btn-group">
        <button
          className={`btn btn-success mx-3 ${activeTab === 'registro' ? 'active' : ''}`}
          onClick={() => handleTabChange('registro')}
        >
          Registro
        </button>
        <button
          className={`btn btn-success mx-3 ${activeTab === 'inicioSesion' ? 'active' : ''}`}
          onClick={() => handleTabChange('inicioSesion')}
        >
          Inicio de Sesión
        </button>
      </div>

      {activeTab === 'registro' && (
        <div>
          <h1>Registro de Usuario</h1>
          <FormRegistro />
        </div>
      )}

      {activeTab === 'inicioSesion' && (
        <div>
          <h1>Inicio Sesión</h1>
          <FormLogin />
        </div>
      )}

      <Chat />
    </div>
  );
};

export default App;
