import { useState, useEffect } from 'react';

function Chat() {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [storedMessages, setStoredMessages] = useState([]);
  const [firstTime, setFirstTime] = useState(false);

  useEffect(() => {
    // SimulaciÃ³n de carga de mensajes guardados en la BDD la primera vez
    if (!firstTime) {
      const storedMessages = [

      ];
      setStoredMessages(storedMessages);
      setFirstTime(true);
    }
  }, [firstTime]);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (message !== '') {
      const newMessage = {
        body: message,
        from: 'Yo',
      };

      setMessages([newMessage, ...messages]);
      setMessage('');
    } else {
      alert('Por favor, ingresa un mensaje antes de enviar.');
    }
  };

  return (
    <div className="App">
      <div className="container mt-3">
        <div className="card shadow border-0">
          <div className="card-body">
            <h5 className="text-center mb-3">CHAT</h5>

            <form onSubmit={handleSubmit}>
              <div className="d-flex">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Mensaje..."
                  onChange={(e) => setMessage(e.target.value)}
                  value={message}
                />
                <button className="btn btn-success mx-3" type="submit">
                  Enviar
                </button>
              </div>
            </form>
          </div>
        </div>

        <div className="card mt-3 mb-3 shadow border-0" id="content-chat">
          <div className="card-body">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`d-flex p-3 ${
                  message.from === 'Yo'
                    ? 'justify-content-end'
                    : 'justify-content-start'
                }`}
              >
                <div
                  className={`card mb-3 shadow border-1 ${
                    message.from === 'Yo' ? 'bg-success bg-opacity-25' : 'bg-light'
                  }`}
                >
                  <div className="card-body">
                    <small className="">{message.from}: {message.body}</small>
                  </div>
                </div>
              </div>
            ))}

            {/* chat stored messages */}
            <small className="text-center text-muted">... Mensajes guardados ...</small>
            {storedMessages.map((message, index) => (
              <div
                key={index}
                className={`d-flex p-3 ${
                  message.from === 'Yo'
                    ? 'justify-content-end'
                    : 'justify-content-start'
                }`}
              >
                <div
                  className={`card mb-3 shadow border-1 ${
                    message.from === 'Yo'
                      ? 'bg-success bg-opacity-25'
                      : 'bg-light'
                  }`}
                >
                  <div className="card-body">
                    <small className="text-muted">{message.from}: {message.body}</small>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Chat;
