import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const FormLogin = () => {
  const [usuario, setUsuario] = useState('');
  const [contraseña, setContraseña] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Lógica para el envío del formulario de login
  };

  return (
    <container>
      <row>
        <div className="col-md-6">
          <h3>Iniciar Sesión</h3>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="usuario" className="form-label">Usuario</label>
              <input type="text" className="form-control" id="usuario"  placeholder="Nombre de usuario" value={usuario} onChange={(e) => setUsuario(e.target.value)} />
            </div>
            <div className="mb-3">
              <label htmlFor="contraseña" className="form-label">Contraseña</label>
              <input type="password" className="form-control" id="contraseña" placeholder= "Contraseña" value={contraseña} onChange={(e) => setContraseña(e.target.value)} />
            </div>
            <button type="submit" className="btn btn-primary">Iniciar sesión</button>
          </form>
        </div>
      </row>
    </container>
  );
};


export default FormLogin;
