import { useState, useEffect } from 'react';

function ChatComponent() {
  const [nickname, setNickname] = useState('');
  const [disabled, setDisabled] = useState(false);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [storedMessages, setStoredMessages] = useState([]);
  const [firstTime, setFirstTime] = useState(false);

  useEffect(() => {
    // Simulación de carga de mensajes guardados en la BDD la primera vez
    if (!firstTime) {
      const storedMessages = [
        { body: 'Mensaje guardado 1', from: 'Usuario1' },
        { body: 'Mensaje guardado 2', from: 'Usuario2' },
        { body: 'Mensaje guardado 3', from: 'Usuario1' },
      ];
      setStoredMessages(storedMessages);
      setFirstTime(true);
    }
  }, [firstTime]);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (nickname !== '') {
      const newMessage = {
        body: message,
        from: 'Yo',
      };

      setMessages([newMessage, ...messages]);
      setMessage('');
    } else {
      alert('Para enviar mensajes debes establecer un nickname!!!');
    }
  };

  const handleNicknameSubmit = (e) => {
    e.preventDefault();
    setNickname(nickname);
    setDisabled(true);
  };

  return (
    <div className="App">
      <div className="container mt-3">
        <div className="card shadow border-0">
          <div className="card-body">
            <h5 className="text-center mb-3">CHAT</h5>

            <form onSubmit={handleNicknameSubmit}>
              <div className="d-flex mb-3">
                <input
                  type="text"
                  className="form-control"
                  id="nickname"
                  placeholder="Nickname..."
                  disabled={disabled}
                  onChange={(e) => setNickname(e.target.value)}
                  value={nickname}
                  required
                />
                <button
                  className="btn btn-success mx-3"
                  type="submit"
                  id="btn-nickname"
                  disabled={disabled}
                >
                  Establecer
                </button>
              </div>
            </form>

            <form onSubmit={handleSubmit}>
              <div className="d-flex">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Mensaje..."
                  onChange={(e) => setMessage(e.target.value)}
                  value={message}
                />
                <button className="btn btn-success mx-3" type="submit">
                  Enviar
                </button>
              </div>
            </form>
          </div>
        </div>

        <div className="card mt-3 mb-3 shadow border-0" id="content-chat">
          <div className="card-body">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`d-flex p-3 ${
                  message.from === 'Yo'
                    ? 'justify-content-end'
                    : 'justify-content-start'
                }`}
              >
                <div
                  className={`card mb-3 shadow border-1 ${
                    message.from === 'Yo' ? 'bg-success bg-opacity-25' : 'bg-light'
                  }`}
                >
                  <div className="card-body">
                    <small className="">{message.from}: {message.body}</small>
                  </div>
                </div>
              </div>
))}
            
{/* chat stored messages */}
<small className="text-center text-muted">... Mensajes guardados ...</small>
{storedMessages.map((message, index) => (
  <div
    key={index}
    className={`d-flex p-3 ${
      message.from === nickname
        ? 'justify-content-end'
        : 'justify-content-start'
    }`}
  >
    <div
      className={`card mb-3 shadow border-1 ${
        message.from === nickname
          ? 'bg-success bg-opacity-25'
          : 'bg-light'
      }`}
    >
      <div className="card-body">
        <small className="text-muted">{message.from}: {message.body}</small>
      </div>
    </div>
  </div>
))}
</div>
</div>
</div>
</div>
);
}

export default ChatComponent;
