import React, { useState } from 'react';
import FormRegistro from './componentes/FormRegistro';
import FormLogin from './componentes/FormLogin';
import Chat from './componentes/Chat';
import ListaUsuarios from './componentes/listaUsuarios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

const App = () => {
  const [activeTab, setActiveTab] = useState('registro');
  const [usuarioLogeado, setUsuarioLogeado] = useState(null);

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  const manejoInicioSesion = (usuario) => {
    setUsuarioLogeado(usuario);
    setActiveTab('chat');
  };

  const manejoCerrarSesion = () => {
    setUsuarioLogeado(null);
    setActiveTab('registro');
  };

  return (
    <div className="container" style={{ position: 'relative', top: '30px' }}>
      <div className="btn-group">
        <button
          className={`btn btn-success mx-3 ${activeTab === 'registro' ? 'active' : ''}`}
          onClick={() => handleTabChange('registro')}
        >
          Registro
        </button>
        <button
          className={`btn btn-success mx-3 ${activeTab === 'inicioSesion' ? 'active' : ''}`}
          onClick={() => handleTabChange('inicioSesion')}
        >
          Inicio de Sesión
        </button>
      </div>

      {activeTab === 'registro' && (
        <div>
          <h1>Registro de Usuario</h1>
          <FormRegistro />
        </div>
      )}

      {activeTab === 'inicioSesion' && (
        <div>
          <h2>Inicio Sesión</h2>
          <FormLogin manejoInicioSesion={manejoInicioSesion} />
        </div>
      )}

      {activeTab === 'chat' && (
        <div>
          <Chat usuarioLogeado={usuarioLogeado} manejoCerrarSesion={manejoCerrarSesion} />
          <div>
            <h3>Usuarios Registrados</h3>
            <ListaUsuarios />
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
