import React, { useState } from "react";

function CambioFotoPerfil({ usuarioLogeado, cambiarFotoPerfil }) {
  const [ , setFotoPerfil] = useState(null);

  const handleChange = (event) => {
    setFotoPerfil(event.target.files[0]);
    
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Aquí puedes realizar la lógica para enviar la nueva foto de perfil al servidor
    // y luego llamar a la función cambiarFotoPerfil para actualizar la foto en el componente Chat

    // Resetear el campo de carga de archivos
    setFotoPerfil(null);
    
  };

  return (
    <div className="card-body">
        <h3 className="card-title"> Cambio de Foto de Perfil</h3>
        <div className="d-flex">
            
            <form onSubmit={handleSubmit}>
                <input type="file" className="form-control" onChange={handleChange} />
                <button className="btn btn-success mx-3" type="submit">Enviar</button>
            </form>
        </div>
    </div>
  );
}

export default CambioFotoPerfil;
