import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { obtenerUsuarios } from '../servicios/clienteaxios';

const FormLogin = () => {
  const [usuario, setUsuario] = useState('');
  const [contraseña, setContraseña] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    try {
      const usuarios = await obtenerUsuarios();
      const usuarioRegistrado = usuarios.find((user) => user.usuario === usuario && user.contraseña === contraseña);
      if (usuarioRegistrado) {
        // Inicio de sesión exitoso
        alert('Inicio de sesión exitoso');
      } else {
        // Credenciales inválidas
        alert('Credenciales inválidas');
      }
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="col-4">
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="usuario" className="form-label">Usuario</label>
          <input type="text" className="form-control" id="usuario" placeholder="Nombre de usuario" value={usuario} onChange={(e) => setUsuario(e.target.value)} />
        </div>
        <div className="mb-3">
          <label htmlFor="contraseña" className="form-label">Contraseña</label>
          <input type="password" className="form-control" id="contraseña" placeholder="Contraseña" value={contraseña} onChange={(e) => setContraseña(e.target.value)} />
        </div>
        <button type="submit" className="btn btn-success mx-3">
          Iniciar sesión
        </button>
      </form>
    </div>
  );
};

export default FormLogin;
