import React, { useState, useEffect } from "react";
import javiImagen from "../imagenes/javi.png";
import { hacerPeticionChats } from '../servicios/clienteAxiosChats';


function Chat({ usuarioSeleccionado, usuarioLogeado }) {
  const [mensaje, setMensaje] = useState("");
  const [mensajes] = useState([]);
  const [mensajesRecibidos, setMensajesRecibidos] = useState([]);
  const [mensajesGuardados, setMensajesGuardados] = useState([]);
  const [primeraVez, setPrimeraVez] = useState(false);

  useEffect(() => {
    if (!primeraVez) {
      const mensajesGuardados = [];
      setMensajesGuardados(mensajesGuardados);
      setPrimeraVez(true);
    }
  }, [primeraVez]);

  const enviarMensaje = async (mensaje) => {
    try {
      const remitente = usuarioLogeado; // ObtÃ©n el usuario logeado desde el estado
      const destinatario = usuarioSeleccionado; // Asigna el destinatario correspondiente, si es necesario
      const contenido = mensaje;
  
      const informacion = {
        remitente: remitente,
        destinatario: destinatario,
        contenido: contenido
      };
  
      await hacerPeticionChats(informacion);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div
      style={{
        position: "fixed",
        top: "20px",
        right: "50px",
        width: "750px",
        maxHeight: "650px",
        overflow: "auto",
      }}
    >
      {usuarioSeleccionado && (
        <div className="text-center mb-3">
          <h3>{usuarioSeleccionado}</h3>
        </div>
      )}

      <div className="container mt-3">
        <div className="card mt-3 mb-3 shadow border-0" id="contenido-chat">
          <div className="card-body">
            {mensajes.map((mensaje, index) => (
              <div
                key={index}
                className={`d-flex p-3 ${
                  mensaje.de === "Yo"
                    ? "justify-content-end"
                    : "justify-content-start"
                }`}
              >
                <div
                  className={`card mb-3 shadow border-1 ${
                    mensaje.de === "Yo"
                      ? "bg-success bg-opacity-25"
                      : "bg-light"
                  }`}
                >
                  <div className="card-body">
                    <div className="d-flex align-items-center">
                      <div className="profile-image">
                        <img src={javiImagen} alt="Imagen de perfil" />
                      </div>
                      <div className="mensaje-texto">
                        <small className="">
                          {mensaje.de}: {mensaje.cuerpo}
                        </small>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}

            <small className="text-center text-muted">
              ... Mensajes guardados ...
            </small>
            {mensajesGuardados.map((mensaje, index) => (
              <div
                key={index}
                className={`d-flex p-3 ${
                  mensaje.de === "Yo"
                    ? "justify-content-end"
                    : "justify-content-start"
                }`}
              >
                <div
                  className={`card mb-3 shadow border-1 ${
                    mensaje.de === "Yo"
                      ? "bg-success bg-opacity-25"
                      : "bg-light"
                  }`}
                ></div>
              </div>
            ))}
          </div>
        </div>
          
        <div className="card shadow border-0">
          <div className="card-body">
            <h5 className="text-center mb-2">Chat</h5>
            <form onSubmit={(e) => {
              e.preventDefault();
              enviarMensaje(mensaje);
              setMensaje("");
            }}>
              <div className="d-flex">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Ingrese un Mensaje..."
                  onChange={(e) => setMensaje(e.target.value)}
                  value={mensaje}
                />
                <button className="btn btn-success mx-3" type="submit">
                  Enviar
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Chat;
