import { useState, useEffect } from 'react';

function Chat() {
  const [mensaje, setMensaje] = useState('');
  const [mensajes, setMensajes] = useState([]);
  const [mensajesGuardados, setMensajesGuardados] = useState([]);
  const [primeraVez, setPrimeraVez] = useState(false);

  useEffect(() => {
    // SimulaciÃ³n de carga de mensajes guardados en la BDD la primera vez
    if (!primeraVez) {
      const mensajesGuardados = [

      ];
      setMensajesGuardados(mensajesGuardados);
      setPrimeraVez(true);
    }
  }, [primeraVez]);

  const enviarMensaje = (e) => {
    e.preventDefault();

    if (mensaje !== '') {
      const nuevoMensaje = {
        cuerpo: mensaje,
        de: 'Yo',
      };

      setMensajes([nuevoMensaje, ...mensajes]);
      setMensaje('');
    } else {
      alert('Por favor, ingresa un mensaje antes de enviar.');
    }
  };

  return (
    <div >
      <div className="container mt-3">
        <div className="card shadow border-0">
          <div className="card-body">
            <h5 className="text-center mb-3">Chat</h5>

            <form onSubmit={enviarMensaje}>
              <div className="d-flex">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Mensaje..."
                  onChange={(e) => setMensaje(e.target.value)}
                  value={mensaje}
                />
                <button className="btn btn-success mx-3" type="submit">
                  Enviar
                </button>
              </div>
            </form>
          </div>
        </div>

        <div className="card mt-3 mb-3 shadow border-0" id="content-chat">
          <div className="card-body">
            {mensajes.map((mensaje, index) => (
              <div
                key={index}
                className={`d-flex p-3 ${
                  mensaje.de === 'Yo'
                    ? 'justify-content-end'
                    : 'justify-content-start'
                }`}
              >
                <div
                  className={`card mb-3 shadow border-1 ${
                    mensaje.de === 'Yo' ? 'bg-success bg-opacity-25' : 'bg-light'
                  }`}
                >
                  <div className="card-body">
                    <small className="">{mensaje.de}: {mensaje.cuerpo}</small>
                  </div>
                </div>
              </div>
            ))}

            {/* chat mensajes guardados */}
            <small className="text-center text-muted">... Mensajes guardados ...</small>
            {mensajesGuardados.map((mensaje, index) => (
              <div
                key={index}
                className={`d-flex p-3 ${
                  mensaje.de === 'Yo'
                    ? 'justify-content-end'
                    : 'justify-content-start'
                }`}
              >
                <div
                  className={`card mb-3 shadow border-1 ${
                    mensaje.de === 'Yo'
                      ? 'bg-success bg-opacity-25'
                      : 'bg-light'
                  }`}
                >
                  <div className="card-body">
                  <small className="text-muted">{mensaje.de}: {mensaje.cuerpo}</small>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Chat;
