import React, { useState } from 'react';
import FormRegistro from './componentes/FormRegistro';
import FormLogin from './componentes/FormLogin';
import Chat from './componentes/Chat';
import ListaUsuarios from './componentes/istaUsuarios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

const App = () => {
  const [logeado, setLogeado] = useState(false);

  const manejoInicioSesion = () => {
    setLogeado(true);
  };

  const manejoCerrarSesion = () => {
    setLogeado(false);
  };

  return (
    <div className="container" style={{ position: 'relative', top: '30px' }}>
      <div className="btn-group">
        {logeado ? (
          <button
            className="btn btn-success mx-3"
            onClick={manejoCerrarSesion}
          >
            Cerrar Sesión
          </button>
        ) : null}
      </div>

      {logeado ? (
        <div>
          <Chat />
          <div>
            <h3>Usuarios Registrados</h3>
            <ListaUsuarios />
          </div>
        </div>
      ) : (
        <div>
          <h1>Registro de Usuario</h1>
          <FormRegistro />
          <h2>Iniciar Sesión</h2>
          <FormLogin onLogin={manejoInicioSesion} />
        </div>
      )}
    </div>
  );
};

export default App;
