import React, { useEffect, useState } from 'react';
import axios from 'axios';


function UserList() {
    const [users, setUsers] = useState([]);
  
    useEffect(() => {
      // Lógica para obtener los usuarios registrados desde el backend
      fetchUsers();
    }, []);
  
    const fetchUsers = async () => {
      try {
        const response = await axios.get('/registro/cuentas'); // Asegúrate de configurar la ruta correcta hacia tu endpoint de usuarios en Django
        setUsers(response.data);
      } catch (error) {
        console.error(error);
      }
    };
  
    return (
      <div>
        <h2>Usuarios Registrados</h2>
        <ul>
          {users.map((user) => (
            <li key={user.id}>{user.username}</li>
          ))}
        </ul>
      </div>
    );
  }
  
  export default UserList;
