import React from 'react';

const FormularioChat = ({ enviarMensaje }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    // Obtén el mensaje desde el formulario
    const mensaje = e.target.mensaje.value;
    // Llama a la función enviarMensaje pasada como prop
    enviarMensaje(mensaje);
    // Limpia el campo de texto después de enviar el mensaje
    e.target.mensaje.value = '';
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="mensaje" placeholder="Ingrese un mensaje..." />
      <button type="submit">Enviar</button>
    </form>
  );
};

export default FormularioChat;
