import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
export const obtenerUsuarios = async () => {
  try {
    const configuracion = crearConfiguracion("get", "cuentas/");
    const respuesta = await axios(configuracion);
    if (respuesta.status === 200) {
      console.log(respuesta.data);
      return respuesta.data;
    }
  } catch (error) {
    console.log(error);
  }
};



const FormLogin = () => {
  const [usuario, setUsuario] = useState('');
  const [contraseña, setContraseña] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

  };

  return (
    <container>
      <row>
        <div className="col-4">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="usuario" className="form-label">Usuario</label>
              <input type="text" className="form-control" id="usuario"  placeholder="Nombre de usuario" value={usuario} onChange={(e) => setUsuario(e.target.value)} />
            </div>
            <div className="mb-3">
              <label htmlFor="contraseña" className="form-label">Contraseña</label>
              <input type="password" className="form-control" id="contraseña" placeholder= "Contraseña" value={contraseña} onChange={(e) => setContraseña(e.target.value)} />
            </div>
            <button type="submit" className="btn btn-success mx-3">Iniciar sesión</button>
          </form>
        </div>
      </row>
    </container>
  );
};


export default FormLogin;
