import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const FormRegistro = () => {
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');
  const [usuario, setUsuario] = useState('');
  const [contraseña, setContraseña] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Lógica para el envío del formulario de registro
  };

  return (
    <container>
      <row>
        <div className="col-md-4 offset-md-4">  
          <h3>Registro de Usuario</h3>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="nombre" className="form-label">Nombre</label>
              <input type="text" className="form-control" id="nombre" value={nombre} onChange={(e) => setNombre(e.target.value)} />
            </div>

            <div className="mb-3">
              <label htmlFor="apellido" className="form-label">Apellido</label>
              <input type="text" className="form-control" id="apellido" value={apellido} onChange={(e) => setApellido(e.target.value)} />
            </div>

            <div className="mb-3">
              <label htmlFor="usuario" className="form-label">Usuario</label>
              <input type="text" className="form-control" id="usuario" placeholder="Nombre de usuario" value={usuario} onChange={(e) => setUsuario(e.target.value)} />
            </div>
            <div className="mb-3">
              <label htmlFor="contraseña" className="form-label">Contraseña</label>
              <input type="password" className="form-control" id="contraseña" value={contraseña} onChange={(e) => setContraseña(e.target.value)} />
            </div>
            <button type="submit" className="btn btn-primary">Registrar</button>
          </form>
        </div>
      </row>
    </container>
  );
};

export default FormRegistro;
