import React, { useState } from "react";
import javiImagen from "../imagenes/javi.png";

function Chat({ usuarioLogeado, usuarioSeleccionado }) {
  const [mensaje, setMensaje] = useState("");
  const [mensajes, setMensajes] = useState([]);

  const enviarMensaje = (e) => {
    e.preventDefault();

    if (mensaje !== "") {
      const nuevoMensaje = {
        cuerpo: mensaje,
        de: usuarioLogeado,
      };

      setMensajes([nuevoMensaje, ...mensajes]);
      setMensaje("");
    } else {
      alert("Por favor, ingresa un mensaje antes de enviar.");
    }
  };

  return (
    <div
      style={{
        position: "fixed",
        top: "20px",
        right: "50px",
        width: "750px",
        maxHeight: "650px",
        overflow: "auto",
      }}
    >
      <div className="text-center mb-3">
        <h3>{usuarioSeleccionado || usuarioLogeado}</h3>
      </div>

      <div className="container mt-3">
        <div className="card mt-3 mb-3 shadow border-0" id="contenido-chat">
          <div className="card-body">
            {mensajes.map((mensaje, index) => (
              <div
                key={index}
                className={`d-flex p-3 ${
                  mensaje.de === (usuarioSeleccionado || usuarioLogeado)
                    ? "justify-content-end"
                    : "justify-content-start"
                }`}
              >
                <div
                  className={`card mb-3 shadow border-1 ${
                    mensaje.de === (usuarioSeleccionado || usuarioLogeado)
                      ? "bg-success bg-opacity-25 text-end"
                      : "bg-light text-start"
                  }`}
                >
                  <div className="card-body">
                    <div className="d-flex align-items-center">
                      <div className="profile-image">
                        <img src={javiImagen} alt="Imagen de perfil" />
                      </div>
                      <div className="mensaje-texto">
                        <small className="">
                          {mensaje.de}: {mensaje.cuerpo}
                        </small>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="card shadow border-0">
          <div className="card-body">
            <h5 className="text-center mb-2">Chat</h5>
            <form onSubmit={enviarMensaje}>
              <div className="d-flex">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Ingrese un Mensaje..."
                  onChange={(e) => setMensaje(e.target.value)}
                  value={mensaje}
                />
                <button className="btn btn-success mx-3" type="submit">
                  Enviar
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Chat;
