import React, { useState } from 'react';
import FormRegistro from './componentes/FormRegistro';
import FormLogin from './componentes/FormLogin';
import Chat from './componentes/Chat';
import ListaUsuarios from './componentes/ListaUsuarios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

const App = () => {
  const [activeTab, setActiveTab] = useState('registro');
  const [usuarioSeleccionado, setUsuarioSeleccionado] = useState(null);
  const [usuarioLogeado, setUsuarioLogeado] = useState(null);

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  const handleUsuarioSeleccionado = (usuario) => {
    setUsuarioSeleccionado(usuario);
  };

  const handleInicioSesionExitoso = (usuario) => {
    setUsuarioLogeado(usuario);
  };

  return (
    <div className="container" style={{ position: 'relative', top: '30px' }}>
      <div className="btn-group">
        <button
          className={`btn btn-success mx-3 ${activeTab === 'registro' ? 'active' : ''}`}
          onClick={() => handleTabChange('registro')}
        >
          Registro
        </button>
        <button
          className={`btn btn-success mx-3 ${activeTab === 'inicioSesion' ? 'active' : ''}`}
          onClick={() => handleTabChange('inicioSesion')}
        >
          Inicio de Sesión
        </button>
      </div>

      {activeTab === 'registro' && (
        <div>
          <h1>Registro de Usuario</h1>
          <FormRegistro />
        </div>
      )}

      {activeTab === 'inicioSesion' && (
        <div>
          <h2>Inicio Sesión</h2>
          <FormLogin onInicioSesionExitoso={handleInicioSesionExitoso} />
        </div>
      )}

      {usuarioLogeado && <Chat usuarioSeleccionado={usuarioSeleccionado} remitente={usuarioLogeado} />}
      <div>
        <ListaUsuarios handleUsuarioSeleccionado={handleUsuarioSeleccionado} />
      </div>
    </div>
  );
};

export default App;
