import React, { useState } from "react";

function CambioFotoPerfil({ usuarioLogeado, cambiarFotoPerfil }) {
  const [fotoPerfil, setFotoPerfil] = useState(null);

  const opcionesFotosPerfil = [
    { id: 1, imagen: require("../imagenes/perfil1.jpg").default },
    { id: 2, imagen: require("../imagenes/perfil2.jpg").default },
    { id: 3, imagen: require("../imagenes/perfil3.jpg").default },
    // Agrega más opciones de fotos de perfil según sea necesario
  ];

  const seleccionarFotoPerfil = (imagen) => {
    setFotoPerfil(imagen);
    cambiarFotoPerfil(imagen);
  };

  return (
    <div>
      <h4>Cambiar Foto de Perfil</h4>
      <div className="fotos-perfil">
        {opcionesFotosPerfil.map((opcion) => (
          <img
            key={opcion.id}
            src={opcion.imagen}
            alt={`Foto de perfil ${opcion.id}`}
            onClick={() => seleccionarFotoPerfil(opcion.imagen)}
          />
        ))}
      </div>
    </div>
  );
}

export default CambioFotoPerfil;
