import React, { useEffect, useState } from "react";
import javiImagen from "../imagenes/javi.png";
import { hacerPeticionChats } from "../servicios/clienteAxiosChats";

function Chat({ usuarioSeleccionado, remitente }) {
  const [mensaje, setMensaje] = useState("");
  const [mensajes, setMensajes] = useState([]);
  const [mensajesGuardados, setMensajesGuardados] = useState([]);
  const [primeraVez, setPrimeraVez] = useState(false);

  useEffect(() => {
    if (!primeraVez) {
      const mensajesGuardados = [];
      setMensajesGuardados(mensajesGuardados);
      setPrimeraVez(true);
    }
  }, [primeraVez]);

  const enviarMensaje = async (e) => {
    e.preventDefault();

    if (mensaje !== "") {
      const nuevoMensaje = {
        cuerpo: mensaje,
        de: remitente,
      };

      setMensajes([nuevoMensaje, ...mensajes]);
      setMensaje("");

      try {
        await hacerPeticionChats(nuevoMensaje);
        console.log("Mensaje enviado correctamente");
      } catch (error) {
        console.log("Error al enviar el mensaje:", error);
      }
    } else {
      alert("Por favor, ingresa un mensaje antes de enviar.");
    }
  };

  return (
    <div
      style={{
        position: "fixed",
        top: "20px",
        right: "50px",
        width: "750px",
        maxHeight: "650px",
        overflow: "auto",
      }}
    >
      {usuarioSeleccionado && (
        <div className="text-center mb-3">
          <h3>{usuarioSeleccionado}</h3>
        </div>
      )}

      <div className="container mt-3">
        <div className="card mt-3 mb-3 shadow border-0" id="contenido-chat">
          <div className="card-body">
            {mensajes.map((mensaje, index) => (
              <div
                key={index}
                className={`d-flex p-3 ${
                  mensaje.de === "Yo"
                    ? "justify-content-end"
                    : "justify-content-start"
                }`}
              >
                <div
                  className={`card mb-3 shadow border-1 ${
                    mensaje.de === "Yo"
                      ? "bg-success bg-opacity-25"
                      : "bg-light"
                  }`}
                >
                  <div className="card-body">
                    <div className="d-flex align-items-center">
                      <div className="profile-image">
                        <img src={javiImagen} alt="Imagen de perfil" />
                      </div>
                      <div className="mensaje-texto">
                        <small className="">
                          {mensaje.de === remitente ? "Yo" : mensaje.de}:{" "}
                          {mensaje.cuerpo}
                        </small>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}

            <small className="text-center text-muted">
              ... Mensajes guardados ...
            </small>
            {mensajesGuardados.map((mensaje, index) => (
              <div
                key={index}
                className={`d-flex p-3 ${
                  mensaje.de === "Yo"
                    ? "justify-content-end"
                    : "justify-content-start"
                }`}
              >
                <div
                  className={`card mb-3 shadow border-1 ${
                    mensaje.de === "                Yo"
                    ? "bg-success bg-opacity-25"
                    : "bg-light"
                  }`}
                >
                  <div className="card-body">
                    <div className="d-flex align-items-center">
                      <div className="profile-image">
                        <img src={javiImagen} alt="Imagen de perfil" />
                      </div>
                      <div className="mensaje-texto">
                        <small className="">
                          {mensaje.de === remitente ? "Yo" : mensaje.de}:{" "}
                          {mensaje.cuerpo}
                        </small>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}

            <form onSubmit={enviarMensaje}>
              <div className="input-group mt-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Escribe un mensaje..."
                  value={mensaje}
                  onChange={(e) => setMensaje(e.target.value)}
                />
                <button
                  className="btn btn-primary"
                  type="submit"
                  disabled={!remitente || !usuarioSeleccionado}
                >
                  Enviar
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Chat;
