import React, { useState } from 'react';
import FormRegistro from './componentes/FormRegistro';
import FormLogin from './componentes/FormLogin';
import Chat from './componentes/Chat';
import ListaUsuarios from './componentes/ListaUsuarios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

const App = () => {
  const [activeTab, setActiveTab] = useState('registro');

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="container" style={{ position: 'relative', top: '30px' }}>
      <div className="row">
        <div className="col-sm-8 col-md-6 col-lg-4">
          <div className="btn-group">
            <button
              className={`btn btn-success ${activeTab === 'registro' ? 'active' : ''}`}
              onClick={() => handleTabChange('registro')}
            >
              Registro
            </button>
            <button
              className={`btn btn-success ${activeTab === 'inicioSesion' ? 'active' : ''}`}
              onClick={() => handleTabChange('inicioSesion')}
            >
              Inicio de Sesión
            </button>
          </div>

          {activeTab === 'registro' && (
            <div>
              <h1>Registro de Usuario</h1>
              <FormRegistro />
            </div>
          )}

          {activeTab === 'inicioSesion' && (
            <div>
              <h2>Inicio Sesión</h2>
              <FormLogin />
            </div>
          )}
        </div>

        <div className="">
          <div className="row">
            <div className="col">
              <Chat />
            </div>
            <div className="col">
              <ListaUsuarios />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
