import React, { useState } from "react";

function CambioFotoPerfil({ usuarioLogeado }) {
  const [fotoPerfil, setFotoPerfil] = useState(null);

  const handleChange = (event) => {
    setFotoPerfil(event.target.files[0]);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Aquí puedes realizar la lógica para enviar la nueva foto de perfil al servidor

    // Resetear el campo de carga de archivos
    setFotoPerfil(null);
  };

  return (
    <div>
      <h3>Cambio de Foto de Perfil</h3>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleChange} />
        <button type="submit">Enviar</button>
      </form>
    </div>
  );
}

export default CambioFotoPerfil;
