import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row , Col} from 'react-bootstrap';
import {guardarZapato, traerZapatos} from './servicios/clienteaxios.js'; //nuevo


function App() {
  const [formulario, setFormulario] = useState({
    marcaZ: '',
    modeloZ: '',
    coloZ: '',
  });

  const [informacion, setInformacion] = useState([
     {'marcaZ':'Nike'
    },
    {
      'marcaZ': 'Adidas'
     },
     {
      'marcaZ': 'Adidas 2'
     }

  ])

  const handleChange = (event) => {
    const temporal = { ...formulario };
    temporal[event.target.name] = event.target.value;
    setFormulario(temporal);
  };

  const handleChangeBuscar = (event) => {
    const temporal = { ...formulario };
    temporal[event.target.name] = event.target.value;
    setFormulario(temporal);
  }

  const guardarZapatos = async() => { //nuevo
    await guardarZapato(formulario)
  }

  const traerZapato = async() => {   //nuevo
    const temporal = await traerZapatos()
    setInformacion(temporal)
  }

  return (
    <div className="App">
      <Container>
        <Form>
          <Form.Text >Formulario con react-django</Form.Text>
            <Form.Group className="mb-3">
              <Form.Label>MarcaZ</Form.Label>
              <Form.Control
                onChange={handleChange}
                name="marcaZ"
                placeholder="Ingrese la marca del zapato."
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
              <Form.Label>ModeloZ</Form.Label>
              <Form.Control
                onChange={handleChange}
                name="modeloZ"
                placeholder="Ingrese el modelo del zapato"
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
              <Form.Label>colorZ</Form.Label>
              <Form.Control
                onChange={handleChange}
                name="coloZ"
                placeholder="ingrese el color del zapato"

              />
            </Form.Group>
            {formulario.marcaZ}
            {formulario.modeloZ}
            {formulario.coloZ}



            <Button onClick={guardarZapatos}>
              Guardar Zapato
            </Button>
        </Form>
        {
          informacion.map((elemento) => { //nuevo
            return <Row>
              <Col>
                {elemento.tallaZ}
              </Col>
              <Col>
                {elemento.marcaZ}
              </Col>
            </Row>
          })
        }
        <Button onClick={traerZapato}> 
          traer Zapatos
        </Button>
        <Form>
          <Form.Group className="mb-3">
            <Form.Label>MarcaZ</Form.Label>
              <Form.Control
                onChange={handleChange}
                name="tallaZ"
                placeholder="Ingrese la marca del zapato."
              />
            </Form.Group>
        </Form>
      </Container>
    </div>
  );
}

export default App;
